'''    https://yq.aliyun.com/articles/228618'''import tensorflow as tfdef save():    tf.reset_default_graph()    h_est = tf.Variable(tf.constant(value=1.01), name='hor_estimate2')    v_est = tf.Variable(tf.constant(value=-2.01), name='ver_estimate2')    # 创建builder    builder = tf.saved_model.builder.SavedModelBuilder('./model/')    # 给builder添加变量并保存    with tf.Session() as sess:        sess.run(tf.global_variables_initializer())        builder.add_meta_graph_and_variables(sess, [tf.saved_model.tag_constants.TRAINING],                                             signature_def_map=None,                                             assets_collection=None)        builder.save()    passdef loader():    with tf.Session() as sess:        tf.saved_model.loader.load(sess, [tf.saved_model.tag_constants.TRAINING], './model/')        h_est = sess.run('hor_estimate2:0')        v_est = sess.run('ver_estimate2:0')        print("h_est: %.2f, v_est: %.2f" % (h_est, v_est))    passif __name__ == '__main__':    loader()    pass